var lincs = require("../lib/lincs.js");
var assert = require('assert');
var couchbase = require('couchbase');

describe('LINCS funtion tests', function () {
    describe('Connectivity', function () {
        it('connects to couchbase', function (done) {
		    lincs.bucket.get("5", function(err, data) {
		    	if(err) throw err;
				assert(data.value.metadata.pert_vehicle === "DMSO");	
				done();
		    });
        });
    });

    describe('LINCS CRUD Functions', function () {
        // note that if promises are used, we need to use .catch because a 
        // failed assert will throw an error and the promise will then never be
        // fulfilled (rather it will be rejected)
        it('retrieves a set of documents', function (done) {
        	this.timeout(50000)
        	var k = []; var c = 0;
        	while(c < 500) {
        		k.push(c);
        		c++;
        	}
		    lincs.get(k, null)
		    .then(function(data) {
				assert(data.length == 500);	
				done();
		    })
			.catch(function(err) {
				done(err);
			});	
        });

        it('retrieves vehicle for an instance', function (done) {
		    lincs.getVehicle("10")
		    .then(function(veh) {
				assert(veh.vehicle === "DMSO");	
				done();
		    })
			.catch(function(err) {
				done(err);
			});	
        });

        it('retrieves the second 100 "gold" instances for cell line A549', function (done) {
	    this.timeout(5000);
		    lincs.getGoldByCell("A549", 100, 100)
		    .then(function(res, err) {
		    	assert(res.length == 100);
		    	done();
		    })
		    .catch(function(e) {
		    });
        });

        it('retrieves all related instances (same cell line, perturbation, dose, duration)', function (done) {
	    this.timeout(5000);
		    lincs.getByPert("A375", "BMP7", 100, 2, 0, 5)
		    .then(function(res) {
		    	assert.equal(res.length , 2);
				done();
		    })
		    .catch(function(e) {
		    	done(e);
		    });
        });



	  it('retrieves all vehicle instances from the same plate for a given instance', function(done) {
	    this.timeout(5000);
		    lincs.instSamePlateVehicles("5")
		    .then(function(res) {
		    	assert.equal(res.length , 13);
				done();
		    })
		    .catch(function(e) {
		    	done(e);
		    });
        });

        it('retrieves gene expression data', function (done) {
	    this.timeout(5000);
		    lincs.getExpression("2")
		    .then(function(res) {
		    	assert(res.data.length  == 978);
				done();
		    })
		    .catch(function(e) {
		    	done(e);
		    });
        });
    });
});
