cell_summary.map:
function (doc, meta) {
    if(doc.metadata.bead_batch) {
      emit([doc.metadata.cell_id, doc.metadata.is_gold], null);
    }
}

pert_summary.map:
function (doc, meta) {
  if(doc.metadata.bead_batch) {
  emit([doc.metadata.pert_type, doc.metadata.pert_id, doc.metadata.is_gold], 
       {pert_desc: doc.metadata.pert_desc,
        pert_id: doc.metadata.pert_id});
  }
}
